import React, { Component } from "react";
import Container from "../Container";
import "./style.css";

class SearchForm extends Component {
  state = {
    female: [],
    male: [],
    result: [],
    error: ""
  }




  render() {
    return (
      <Container>
        <form>
          <div className="form-group">
            <br />
            <button onClick={this.renderGender} id="male" className="btn btn-dark male">
              Male
        </button>
            <button onClick={this.renderGender} id="female" className="btn btn-dark female">
              Female
        </button>
          </div>
        </form>

        <table className="styleTable">
          <tr>
            <th onClick={this.sortByName}>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>Phone Number</th>
            <th>Email</th>
          </tr>
          {this.state.result.map(info => {
            return <tr>
              <th>{info.name.first}</th>
              <th>{info.name.last}</th>
              <th>{info.dob.age}</th>
              <th>{info.phone}</th>
              <th>{info.email}</th>
            </tr>
          })}
        </table>
      </Container>
    );
  }
}

export default SearchForm;
